<script>
import anime from "animejs";
import GreenTick from "@/components/feelingsQuestion/ticks/GreenTick";

export default {
  name: "Session1Page21",
  components: {GreenTick},
  data() {
    return {
      correctAns: 0,
      animalIndex: 0,
      animalArray: ['duck','cow','pig','sheep']
    }
  },
  methods: {
    playSoundButton() {
      switch (this.animalArray[this.animalIndex]) {
        case 'duck':
          this.$refs.duck.play();
          break;
        case 'cow':
          this.$refs.cow.play();
          break;
        case 'pig':
          this.$refs.pig.play();
          break;
        case 'sheep':
          this.$refs.sheep.play();
          break;
      }
    },
    playSound() {
      switch (this.animalArray[this.animalIndex]) {
        case 'duck':
          setTimeout(() => {
            this.$refs.duck.play()
          }, 600 * 4)
          break;
        case 'cow':
          setTimeout(() => {
            this.$refs.cow.play()
          }, 600)
          break;
        case 'pig':
          setTimeout(() => {
            this.$refs.pig.play()
          }, 600)
          break;
        case 'sheep':
          setTimeout(() => {
            this.$refs.sheep.play()
          }, 600)
          break;
      }
    },
    checkAns(animal, event) {
      if (animal === this.animalArray[this.animalIndex]) {
        this.handleCorrectAnswer(event.target)
      }
      else {
        this.handleWrongAnswer()
      }
    },
    handleCorrectAnswer(target) {
      this.animateGreenTick(this.convertTick(target));
      target.style.visibility = 'hidden'
      this.$refs.correct.play();
      this.correctAns++;
      this.animalIndex++;
      this.playSound();
    },
    handleWrongAnswer() {
      anime({
        targets: '.sticker',
        easing: 'linear',
        opacity: [
          {value: 1, duration: 200},
          {value: 0, duration: 200, delay: 1000},
        ]
      })
      this.$refs.wrong.play();
    },
    convertTick(maskId) {
      switch (maskId.id) {
        case 'mask-1':
          return '#tick-1'
        case 'mask-2':
          return '#tick-2'
        case 'mask-3':
          return '#tick-3'
        case 'mask-4':
          return '#tick-4'
      }
    },
    animateGreenTick(target) {
      anime({
        targets: target,
        opacity: 1,
        easing: 'linear',
        duration: 300,
      })
    },
    animateTextBox() {
      let textBox = document.querySelector('.text-box');
      textBox.style.zIndex = '10';
      anime({
        targets: textBox,
        opacity: 1,
        duration: 300,
        easing: 'linear'
      })
    }
  },
  mounted() {
    let images = document.querySelectorAll('.images')
    let animation = anime.timeline({
      easing: 'linear',
      duration: 300,
      delay: 300
    })
    animation
      .add({
        targets: images[1],
        opacity: 1
      })
      .add({
        targets: images[0],
        opacity: 1
      })
      .add({
        targets: images[3],
        opacity: 1
      })
      .add({
        targets: images[2],
        opacity: 1
      })

    this.playSound();
  },
  watch: {
    correctAns: function () {
      if (this.correctAns === 4) {
        document.querySelector('.reward').style.opacity = '1';
        anime({
          targets: '.reward',
          scale: 20,
          duration: 1000,
        })
        this.$refs.celebrate.play()
        this.animateTextBox();
      }
    }
  }
}
</script>

