<script>
import ImageComponent from "@/components/imageComponent/ImageComponent.vue";
import anime from "animejs";
import EmotionPickInstruction from "@/components/instructions/emotionPickInstruction/EmotionPickInstruction";
import FeelingQuestion from "@/components/feelingsQuestion/FeelingsQuestion";

export default {
  name: "Session2Page12",
  components: {FeelingQuestion, EmotionPickInstruction, ImageComponent},
  methods: {
    handleCorrectAnswer() {
      document.querySelector('.reward').style.opacity = '1';
      anime({
        targets: '.reward',
        scale: 20,
        duration: 1000,
      });
      this.$refs.goodTry.pause();
      setTimeout(() => {this.$refs.veryGood.play()}, 500)
    },
    handleWrongAnswer() {
      anime({
        targets: '.sticker',
        easing: 'linear',
        opacity: [
          {value: 1, duration: 200},
          {value: 0, duration: 200, delay: 1000},
        ]
      });
      setTimeout(() => {this.$refs.goodTry.play()}, 500)
    },
    playVoiceOver() {
      setTimeout(() => {this.$refs.voice.play()}, 500)
    },
    playSoundText() {
      this.playVoiceOver();
      this.$refs.question.animateText();
    }
  },
  mounted() {}
}
</script>

