<script>
import TrafficLight from "@/components/trafficLights/TrafficLights";
import anime from "animejs";
export default {
  name: 'Session5Page6',
  components: {TrafficLight},
  methods: {
    handleWrongAnswer() {
      anime({
        targets: '.sticker',
        easing: 'linear',
        opacity: [
          {value: 1, duration: 200},
          {value: 0, duration: 200, delay: 1000},
        ]
      })
    },
    handleCorrectAnswer() {
      document.querySelector('.reward').style.opacity = '1';
      anime({
        targets: '.reward',
        scale: 20,
        duration: 1000,
      })
    }
  },
  mounted() {
    let animation = anime.timeline({
      easing: 'linear',
      duration: 500,
      delay: 200,
    })
    animation
      .add({
        targets: '.text-box',
        opacity: 1
      })
      .add({
        targets: '.traffic-light',
        opacity: 1
      })
  }
}
</script>

