<script>
import ImageComponent from "@/components/imageComponent/ImageComponent.vue";
import anime from "animejs";

export default {
  name: 'Session6Page5',
  components: {ImageComponent},
  data() {
    return {
      data: [{id: 1, complete: false, show: true, type: "red", text: "My classmates will make fun of me."},
        {id: 2, complete: false, show: false, type: "green", text: "My teacher can help me."},
        {id: 3, complete: false, show: false, type: "red", text: "I do not like standing here alone, I am worried."},
        {id: 4, complete: false, show: false, type: "green", text: "I can be brave, smile, and look people in the eye."},
        {id: 5, complete: false, show: false, type: "red", text: "I might stutter."},
        {id: 6, complete: false, show: false, type: "green", text: "I can do deep, slow breathing to calm down."},
      ],
      currentQuestionProgress: 1,
      currentAnswerProgress: 0,
      currentVoice: null
    }
  },
  methods: {
    animateText() {
      let text = document.getElementsByClassName('text-box')[0].children;
      let animation = anime.timeline({easing: 'linear', duration: 500})
      animation
        .add({targets: text[1], opacity: 1}, 7000)
        .add({targets: text[2], opacity: 1}, 13000)
        .add({targets: text[3], opacity: 1}, 19000);
    },
    playVoiceOver() {
      setTimeout(() => {this.$refs.voice.play()}, 500)
    },
    playSoundText() {
      this.playVoiceOver();
      this.animateText();
    },
    animateLight(lightName) {
      anime({
        targets: '.'+lightName+'-light',
        opacity: [
          {value: 0, duration: 200},
          {value: 1, duration: 200, delay: 100},
          {value: 0, duration: 200},
          {value: 1, duration: 200, delay: 100},
        ],
        easing: 'linear'
      });
    },
    lightChosen(lightName) {
      this.currentVoice.pause();
      this.$refs.clickSound.play();
      this.animateLight(lightName);
      if (lightName === this.data[this.currentQuestionProgress - 1].type) {
        this.data[this.currentQuestionProgress - 1].complete = true;
        if (this.currentQuestionProgress === 6) {
          this.playCelebrateSounds();
          this.currentAnswerProgress++;
          this.disableInteraction();
        } else {
          this.$refs.correctVoice.play();
          this.currentQuestionProgress++;
          this.currentAnswerProgress++;
          setTimeout(() => {
              this.data[this.currentQuestionProgress - 1].show = true;
              this.currentVoice = document.querySelector('#voice-option-'+this.currentQuestionProgress);
              this.currentVoice.play();
            }, 1500
          );
        }
      } else {
        this.playIncorrectSounds();
      }
    },
    disableInteraction() {
      document.querySelector('.masks-container').style.visibility = 'hidden';
    },
    playIncorrectSounds() {
      this.$refs.wrong.play();
      this.$refs.wrongVoice.play();
      anime({
        targets: '.sticker',
        easing: 'linear',
        opacity: [
          {value: 1, duration: 200},
          {value: 0, duration: 200, delay: 1000},
        ]
      })
    },
    playCelebrateSounds() {
      this.$refs.correct2.play();
      this.$refs.celebrate.play();
      document.querySelector('.reward').style.opacity = '1';
      anime({
        targets: '.reward',
        scale: 20,
        duration: 1000,
      })
    },
  },
  mounted() {this.currentVoice = this.$refs.voice;},
}
</script>

