<script>
import anime from "animejs";
export default {
  name: 'Session7Page10',
  data() {
    return {
      step:null,
      correctAns:[],
      ansArray: [
        {id: 1, ans: 'Lorem 1 ipsum dolor sit amet, consectetur adipiscing elit. Quisque non tristique urna.'},
        {id: 2, ans: 'Lorem 2 ipsum dolor sit amet, consectetur adipiscing elit. Quisque non tristique urna.'},
        {id: 3, ans: 'Lorem 3 ipsum dolor sit amet, consectetur adipiscing elit. Quisque non tristique urna.'},
        {id: 4, ans: 'Lorem 4 ipsum dolor sit amet, consectetur adipiscing elit. Quisque non tristique urna.'},
        {id: 5, ans: 'Lorem 5 ipsum dolor sit amet, consectetur adipiscing elit. Quisque non tristique urna.'},
      ],
    }
  },
  methods: {
    shuffle() {
      this.ansArray.sort(() => Math.random() - 0.5);
    },
    animateElements() {
      let helper = document.querySelector('#left-container');
      let reward = document.querySelector('#right-container');
      let trophy = document.querySelector('.trophy-container');
      let plan = document.querySelectorAll('.plans');
      let animation = anime.timeline({
        duration: 1,
        easing: 'linear'
      })
      animation
        .add({targets: helper, opacity: 1}, 1)
        .add({targets: plan, opacity: 1}, 1)
        .add({targets: trophy, opacity: 1}, 1)
        .add({targets: reward, opacity: 1}, 1)
      // animation
      //   .add({targets: helper, opacity: 1}, 6300)
      //   .add({targets: plan, opacity: 1}, 10100)
      //   .add({targets: trophy, opacity: 1}, 13300)
      //   .add({targets: bubble, opacity: 1}, 16800)
      //   .add({targets: reward, opacity: 1}, 21500)
    },
    setAudioVolumeLevel(level) {
      this.$refs.audio.volume = level
    },
    playVoiceOver() {
      setTimeout(() => {this.$refs.voice.play()}, 500)
    },
    revealChoices(id) {
      this.animateClickActionButton('#reveal-btn-'+id);
      document.querySelector('.choices-container').style.visibility = 'visible';
      this.step = id;
    },
    answerChosen(id, text) {
      let stepId = "#step-"+ this.step;
      let stepContainer = document.querySelector(stepId).children[2];
      stepContainer.innerHTML = this.step + ") " + text;
      document.querySelector('.choices-container').style.visibility = 'hidden';
      this.checkCorrectAns(id);
    },
    checkCorrectAns(id) {
      if (this.step === id) {
        if (!this.correctAns.includes(this.step)) {
          this.correctAns.push(id);
        }
      } else {
        if (this.correctAns.includes(this.step)) {
          let index = this.correctAns.indexOf(this.step);
          if (index !== -1) {
            this.correctAns.splice(index, 1);
          }
        }
      }
    },
    checkComplete() {
      this.animateClickActionButton('.check-btn');
      if (this.correctAns.length === 5) {
        this.$refs.correct.play();
        document.querySelector('.reward').style.opacity = '1';
        anime({
          targets: '.reward',
          scale: 20,
          duration: 1000,
        });
        let buttons = document.querySelectorAll('.button-container');
        buttons.forEach(button => {button.style.visibility = 'hidden';})
        setTimeout(() => {this.$refs.veryGood.play()}, 500);
      } else {
        this.$refs.wrong.play();
        anime({
          targets: '.sticker',
          easing: 'linear',
          opacity: [
            {value: 1, duration: 200},
            {value: 0, duration: 200, delay: 1000},
          ]
        });
        setTimeout(() => {this.$refs.goodTry.play()}, 500);
      }
    },
    animateClickActionButton(button) {
      anime({
        targets: button,
        scale: [
          {value: 0.6, duration: 100},
          {value: 1, duration: 100},
        ],
        easing: 'linear'
      });
    },
  },
  created() {
    this.shuffle();
    console.log(this.ansArray);
  },
  mounted() {
    this.animateElements();
    this.setAudioVolumeLevel(0.2);
    this.playVoiceOver();
  }
}
</script>

