<script>
import anime from "animejs";
import draggable from "vuedraggable";

export default {
  name: 'Session7Page10',
  components: {draggable},
  data() {
    return {
      step:null,
      wrongAns: [],
      correctAns: 0,
      choice1: [],
      choice2: [],
      choice3: [],
      choice4: [],
      choice5: [],
      orders: [],
      timeVariable: [],
      timeout: [4100, 5200, 5300, 5600, 6400],
      ansArray: [
        {id: 1, ans: 'Visit my new school.'},
        {id: 2, ans: 'Walk to school a few times.'},
        {id: 3, ans: 'Ride my bike to\nschool a few times.'},
        {id: 4, ans: 'Meet children who\ngo to the same school.'},
        {id: 5, ans: 'Start school with a big\nsmile and a brave voice.'},
      ],
    }
  },
  methods: {
    shuffle() {
      this.ansArray.sort(() => Math.random() - 0.5);
    },
    animateText() {
      let plan = document.querySelectorAll('.plans');
      let choices = document.querySelectorAll('.choices-container');
      let animation = anime.timeline({duration: 1, easing: 'linear'})
      animation
        .add({targets: '.trophy-container', opacity: 1}, 1000)
        .add({targets: plan, opacity: 1}, 1000)
        .add({targets: '.instruction-container', opacity: 1}, 4700)
        .add({targets: choices, opacity: 1}, 12000)
    },
    setAudioVolumeLevel(level) {
      this.$refs.audio.volume = level
    },
    playVoiceOver() {
      setTimeout(() => {this.$refs.voice.play()}, 500);
      this.timeVariable[0] = setTimeout(() => document.querySelector('#step-audio-'+this.orders[0]).play(),12500);
      this.timeVariable[1] = setTimeout(() => document.querySelector('#step-audio-'+this.orders[1]).play(),12500 + this.timeout[this.orders[0] - 1]);
      this.timeVariable[2] = setTimeout(() => document.querySelector('#step-audio-'+this.orders[2]).play(),12500 + this.timeout[this.orders[0] - 1] + this.timeout[this.orders[1] - 1]);
      this.timeVariable[3] = setTimeout(() => document.querySelector('#step-audio-'+this.orders[3]).play(),12500 + this.timeout[this.orders[0] - 1] + this.timeout[this.orders[1] - 1] +this.timeout[this.orders[2] - 1]);
      this.timeVariable[4] = setTimeout(() => document.querySelector('#step-audio-'+this.orders[4]).play(),12500 + this.timeout[this.orders[0] - 1] + this.timeout[this.orders[1] - 1] +this.timeout[this.orders[2] - 1] + this.timeout[this.orders[3] - 1]);
      this.timeVariable[5] = setTimeout(() => document.querySelector('.question-mask').style.visibility = 'hidden',12500 + this.timeout[this.orders[0] - 1] + this.timeout[this.orders[1] - 1] +this.timeout[this.orders[2] - 1] + this.timeout[this.orders[3] - 1] + this.timeout[this.orders[4] - 1]);
    },
    handleChoice1(args) {
      if (args.added) {
        let stepId = 1;
        let element = args.added.element;
        if (element.id !== stepId) {
          this.handleWrongAnswer(stepId);
          let ele = this.choice1.pop();
          this.ansArray.push(ele);
        } else {
          this.handleCorrectAnswer(stepId);
        }
      }
    },
    handleChoice2(args) {
      if (args.added) {
        let stepId = 2;
        let element = args.added.element;
        if (element.id !== stepId) {
          this.handleWrongAnswer(stepId);
          let ele = this.choice2.pop();
          this.ansArray.push(ele);
        } else {
          this.handleCorrectAnswer(stepId);
        }
      }
    },
    handleChoice3(args) {
      if (args.added) {
        let stepId = 3;
        let element = args.added.element;
        if (element.id !== stepId) {
          this.handleWrongAnswer(stepId);
          let ele = this.choice3.pop();
          this.ansArray.push(ele);
        } else {
          this.handleCorrectAnswer(stepId);
        }
      }
    },
    handleChoice4(args) {
      if (args.added) {
        let stepId = 4;
        let element = args.added.element;
        if (element.id !== stepId) {
          this.handleWrongAnswer(stepId);
          let ele = this.choice4.pop();
          this.ansArray.push(ele);
        } else {
          this.handleCorrectAnswer(stepId);
        }
      }
    },
    handleChoice5(args) {
      if (args.added) {
        let stepId = 5;
        let element = args.added.element;
        if (element.id !== stepId) {
          this.handleWrongAnswer(stepId);
          this.choice5.pop();
          this.ansArray.push(element);
        } else {
          this.handleCorrectAnswer(stepId);
        }
      }
    },
    handleCorrectAnswer(stepId) {
      this.correctAns++;
      let greenTickId = '#green-tick-' + stepId;
      anime({
        targets: greenTickId,
        easing: 'linear',
        opacity: 1,
        duration: 500
      })
      let maskId = '#mask-' + stepId;
      document.querySelector(maskId).style.zIndex = '100';
      this.$refs.correct.play();
    },
    handleWrongAnswer(stepId) {
      let redTickId = '#red-tick-' + stepId;
      anime({
        targets: redTickId,
        easing: 'linear',
        opacity: [
          {value: 1, duration: 200},
          {value: 0, duration: 100},
          {value: 1, duration: 200},
          {value: 0, duration: 100, delay: 500},
        ]
      });
      anime({
        targets: '.sticker',
        easing: 'linear',
        opacity: [
          {value: 1, duration: 200},
          {value: 0, duration: 200, delay: 1000},
        ]
      });
      this.$refs.wrong.play();
      setTimeout(() => {this.$refs.goodTry.play()}, 500)
    },
    rewardFunction() {
      document.querySelector('.reward').style.opacity = '1';
      anime({
        targets: '.reward',
        scale: 20,
        duration: 1000,
      })
      this.$refs.celebrate.play();
      setTimeout(() => {this.$refs.veryGood.play()}, 500)
    },
    playSoundText() {
      this.playVoiceOver();
      this.animateText();
    }
  },
  created() {
    this.shuffle();
    this.orders = [this.ansArray[0].id,this.ansArray[1].id,this.ansArray[2].id,this.ansArray[3].id,this.ansArray[4].id];
  },
  mounted() {
    this.setAudioVolumeLevel(0.2);
  },
  destroyed() {
    this.timeVariable.forEach(time => clearTimeout(time));
  },
  watch: {
    correctAns: function() {
      if (this.correctAns < 5) {
        setTimeout(() => {this.$refs.correctVoice.play()}, 500);
        this.$refs.goodTry.pause()
      }
      else if (this.ansArray.length === 0 && this.correctAns === 5) {
        this.rewardFunction();
      }
    }
  }
}
</script>

