<script>
import ImageComponent from "@/components/imageComponent/ImageComponent.vue";
import anime from "animejs";

export default {
  name: 'Session8Page11_1',
  data() {
    return {
      bubbleArray: [],
      currentAudio: null,
    }
  },
  components: {ImageComponent},
  methods: {
    animateText() {
      let animation = anime.timeline({duration: 500, easing: 'linear'})
      animation
        .add({targets: '.text-box', opacity: 0.9}, 500)
        .add({targets: '.thought-bubbles-container', opacity: 1}, 3000)
    },
    setAudioVolumeLevel(level) {
      this.$refs.audio.volume = level;
    },
    playVoiceOver() {
      setTimeout(() => {this.$refs.voice.play()}, 500)
    },
    readText(id) {
      if (this.currentAudio !== null) this.currentAudio.pause();
      this.currentAudio = document.querySelector('#thought-audio-'+id);
      this.currentAudio.play();
    },
    revealText(id) {
      if (!this.bubbleArray.includes(id)) {
        this.$refs.voice.pause();
        let shape = document.querySelectorAll('.shape-'+id);
        let text = document.querySelector('#text-'+id);
        anime({
          targets: shape,
          duration: 200,
          fill: '#00CE7C',
          easing: 'linear'
        });
        anime({
          targets: text,
          duration: 200,
          opacity: 1,
          easing: 'linear'
        });
        this.readText(id);
        this.$refs.clickSound.play();
        this.bubbleArray.push(id);
        this.playRewardSound();
      }
    },
    playRewardSound() {
      if (this.bubbleArray.length === 7) this.$refs.cheer.play();
    },
    playSoundText() {
      this.playVoiceOver();
      this.animateText();
    }
  },
  mounted() {
    this.setAudioVolumeLevel(0.4);
  }
}
</script>

